import { Player } from "@remotion/player";

import CardFlip from "../ui/card-flip";

export default function CardFlipDemo() {
  const cardFlipProps = {
    frontText: "Hello!",
    backText: "Remotion!",
    frontTextColor: "#FFFFFF",
    backTextColor: "#FFFFFF",
    frontBackgroundColor: "linear-gradient(45deg, #6d28d9, #be185d)",
    backBackgroundColor: "linear-gradient(45deg, #be185d, #6d28d9)",
    fontSize: "2rem",
    width: "280px", // Slightly smaller for demo
    height: "380px", // Slightly smaller for demo
    borderRadius: "15px",
    durationInFrames: 90, // Slower flip for demo
    damping: 15,
    mass: 0.5,
    stiffness: 80,
  };

  return (
    <Player
      component={CardFlip}
      inputProps={cardFlipProps}
      durationInFrames={120} // Total duration of the player timeline
      compositionWidth={480}
      compositionHeight={320} // Adjusted for card aspect ratio
      fps={30}
      style={{
        width: "100%",
        height: "100%",
      }}
      autoPlay
      controls
      loop
    />
  );
}
