"use client";

import { spring, useCurrentFrame, useVideoConfig } from "remotion";

interface CardFlipProps {
  frontText?: string;
  backText?: string;
  frontTextColor?: string;
  backTextColor?: string;
  frontBackgroundColor?: string;
  backBackgroundColor?: string;
  fontSize?: string;
  width?: string;
  height?: string;
  borderRadius?: string;
  durationInFrames?: number;
  damping?: number;
  mass?: number;
  stiffness?: number;
}

export default function CardFlip({
  frontText = "Remotion ðŸ‘‹",
  backText = "Back",
  frontTextColor = "white",
  backTextColor = "white",
  frontBackgroundColor = "linear-gradient(45deg, #1e3a8a, #3b82f6)",
  backBackgroundColor = "linear-gradient(45deg, #1e3a8a, #3b82f6)",
  fontSize = "2rem",
  width = "300px",
  height = "400px",
  borderRadius = "20px",
  durationInFrames = 60, // Default duration for a full flip
  damping = 15,
  mass = 0.5,
  stiffness = 100, // Added stiffness for consistency
}: CardFlipProps) {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  const rotation = spring({
    frame,
    fps,
    from: 0,
    to: 360,
    durationInFrames,
    config: {
      damping,
      mass,
      stiffness,
    },
  });

  return (
    <div
      style={{
        position: "absolute",
        top: "50%",
        left: "50%",
        perspective: "1000px", // Ensures 3D effect
        transform: "translate(-50%, -50%)", // Center the whole container
      }}
    >
      <div
        style={{
          width,
          height,
          transform: `rotateY(${rotation}deg)`,
          transformStyle: "preserve-3d",
          position: "relative",
        }}
      >
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backfaceVisibility: "hidden",
            background: frontBackgroundColor,
            borderRadius,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            fontSize,
            fontWeight: "bold",
            color: frontTextColor,
          }}
        >
          {frontText}
        </div>
        <div
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            backfaceVisibility: "hidden",
            background: backBackgroundColor,
            borderRadius,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            fontSize,
            fontWeight: "bold",
            color: backTextColor,
            transform: "rotateY(180deg)",
          }}
        >
          {backText}
        </div>
      </div>
    </div>
  );
}
